import React, { useEffect, useState } from 'react'
import TextField from '@mui/material/TextField';
import Auth from './Auth';
import { Link, useNavigate } from 'react-router-dom';
import { toast } from "react-toastify";
import BackdropLoader from '../Layouts/BackdropLoader';
import { useDispatch, useSelector } from 'react-redux';
import { clearErrors, loginUser } from '../../actions/userAction';

const Login = () => {

    const dispatch = useDispatch();
    const navigate = useNavigate();

    const { loading, isAuthenticated, error, user } = useSelector((state) => state.user);

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const handleLogin = (e) => {
        e.preventDefault();
        dispatch(loginUser(email, password));
    }

    useEffect(() => {
        if (error) {
            toast.error(error);
            dispatch(clearErrors());
        }
        if (isAuthenticated) {
            navigate(`/${user.username}`)
        }
    }, [dispatch, error, isAuthenticated, navigate]);


    return (
        <>
            {loading && <BackdropLoader />}
            <Auth>
                <div className="bg-white border flex flex-col gap-2 p-4 pt-10">
                    <img draggable="false" className="mx-auto h-30 w-36 object-contain" src="https://mygvbuck.s3.us-east-1.amazonaws.com/5e4491ef61f10082d264e9884e8928d6.png?response-content-disposition=inline&X-Amz-Security-Token=IQoJb3JpZ2luX2VjEEcaCmFwLXNvdXRoLTEiRzBFAiAM7wjOw9qsViF1eGMB7%2FEQw8fNmlxPlzhv6K071EYmjQIhAKRZUJDcExeQrDvRpAVlGZh%2Fd1zXGsOrJlHTo8QTDp8ZKu0CCND%2F%2F%2F%2F%2F%2F%2F%2F%2F%2FwEQABoMODExODgxMjQ5NzgzIgyInX3A2E0O%2ByV7EjkqwQKuJtrXH2i1MgyYWx%2FjrAZf8lwm3i1VPr7NC8wqO9gmu6CtcxF%2F79kysfOLlz0xyqPWumvzfPNQ9DYb6Xa7QSXk%2B1lBBdXIUuu5memnogFSOEmOSdn0ygJj%2BVMObgTea98QQMgVMcUa2vICzfa24PWPFJi6bxa1B83ufvPVjRQME0PELI9ZthkysDgvPlRki3Q2mTDU53FTBYE4w2CucS%2Fwwa4af1%2BZSPDxVtKQEtT4BagaVK%2FlYyHCbrT%2FNzYUAqDyPQIm9Y39JEFBd12hZKQdXfY2Oz1z3WSGLeRdVjuphL9NogihZ7Ckt52BjRbosbkdtXtXI%2FTs38yq%2FiIOhVkWhswJ6CRTp8baUOmWZmrxMwj6U4QlU%2FAQo85z6fqk%2Fe%2FhGjQ2mDNgFxjn8De3WDG9WNWJWott7Ips9NpovPCT4rYwuMTWmAY6swKjPFawDVAo3N9uGB6nuVk9GScauiPWQnf%2Bllh%2BHGi6BwfxJ74MTtisR5Tbn%2FrLnZqEEX9%2BHKxocuWE7ZUElRIsfceOzaMKZyPImC39ujOMfq5IwaIJwTdH1Mn2Il54QF%2BlQ29x0MzUVA5WyX2JjpTwEBni65b9OYGN%2Fakovf5e%2FMovbrEhaW1EkAQdrqxc6jl17JYrBZfcwxjbVrup8LcfcLvp%2FFpCXOhEVYI3RQI%2BXU1ET8XnkavoIPJl1nalNpn3GRdZiNuhRWf821D5QmPHXo8wg9rt9FbfM03dxsiZg9VL8dCh5ONZvE9sEwlcnIURCsPNbb4UHUfbIDi1uh4MP2w8uI00EW1W94XGjIa7TQSqPdajoIgAvhX8D5QiAnJFlPFic9zQ8X3VG7EWnfceFmS6&X-Amz-Algorithm=AWS4-HMAC-SHA256&X-Amz-Date=20220905T114627Z&X-Amz-SignedHeaders=host&X-Amz-Expires=300&X-Amz-Credential=ASIA32B7EXP3YMYAQP5S%2F20220905%2Fus-east-1%2Fs3%2Faws4_request&X-Amz-Signature=34ec5267aca04ccabd1801ac217505cbf41b585698ba650c08d656f17fb75e74" alt="" />
                    <form onSubmit={handleLogin} className="flex flex-col justify-center items-center gap-3 m-3 md:m-8">
                        <TextField
                            label="Email/Username"
                            type="text"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                            size="small"
                            fullWidth
                        />
                        <TextField
                            label="Password"
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                            size="small"
                            fullWidth
                        />
                        <button type="submit" className="bg-primary-blue font-medium py-2 rounded text-white w-full">Log In</button>
                        <span className="my-3 text-gray-500">OR</span>
                        <Link to="/password/forgot" className="text-sm font-medium text-blue-800">Forgot password?</Link>
                    </form>
                </div>

                <div className="bg-white border p-5 text-center">
                    <span>Don't have an account? <Link to="/register" className="text-primary-blue">Sign up</Link></span>
                </div>
            </Auth>
        </>
    )
}

export default Login
